mapper.select:
select `id`, `owner`, `repo`, `display_name`, `period_type`, `period`, null as `rank`, `openrank`, `stars`, null as `delta_stars`, null as `attention`, null as `activity`, `description`, `tags`, `updated_at` from repo where period_type = #{periodType} and period = #{period}
order by `openrank` desc order by `stars` desc order by `openrank` desc
limit #{limit}
